mapper.select:
SELECT * FROM ( SELECT ROWNUM AS rnum, studyList.* FROM ( SELECT ts.STUDY_NUM, tu.USER_NUM, ts.STUDY_TITLE, ts.STUDY_DESCRIPT, ts.STUDY_LOCATION, ts.STUDY_START_DAY, ts.STUDY_END_DAY, ts.STUDY_START_TIME, ts.STUDY_END_TIME, ts.STUDY_LIMIT, ts.STUDY_DEADLINE, ts.STUDY_SHOWPH, ts.STUDY_CATEGORY, ts.STUDY_UPLOAD_DATE, ts.STUDY_EDIT_DATE, tu.USER_ID, tu.USER_NICK, tu.USER_PH, (SELECT COUNT(*) FROM TBL_STUDY_APPLY tsa WHERE tsa.STUDY_NUM = ts.STUDY_NUM) AS participants FROM TBL_STUDY ts INNER JOIN TBL_USER tu ON ts.USER_NUM = tu.USER_NUM
WHERE ts.STUDY_TITLE LIKE '%' || #{keyword} || '%'
ORDER BY STUDY_NUM DESC ) studyList WHERE ROWNUM <= #{endRow} ) WHERE rnum >= #{startRow}

mapper.insert:
INSERT INTO TBL_STUDY (
STUDY_NUM
, USER_NUM
, STUDY_TITLE
, STUDY_DESCRIPT
, STUDY_LOCATION
, STUDY_START_DAY
, STUDY_END_DAY
, STUDY_START_TIME
, STUDY_END_TIME
, STUDY_LIMIT
, STUDY_DEADLINE
, STUDY_SHOWPH
, STUDY_CATEGORY
, STUDY_UPLOAD_DATE
, STUDY_EDIT_DATE) 
VALUES (
SEQ_STUDY.NEXTVAL
, #{userNum}
, #{studyTitle}
, #{studyDescript}
, #{studyLocation}
, TO_DATE(#{studyStartDay}, 'YYYY-MM-DD HH24:MI:SS')
, TO_DATE(#{studyEndDay}, 'YYYY-MM-DD HH24:MI:SS')
, #{studyStartTime}
, #{studyEndTime}
, #{studyLimit}
, #{studyDeadline}
, #{studyShowph}
, #{studyCategory}
, SYSDATE
, SYSDATE
)

mapper.update:
UPDATE TBL_STUDY
SET 
STUDY_TITLE = #{studyTitle}
, STUDY_DESCRIPT = #{studyDescript}
, STUDY_LOCATION = #{studyLocation}
, STUDY_START_DAY = TO_DATE(#{studyStartDay}, 'YYYY-MM-DD HH24:MI:SS')
, STUDY_END_DAY = TO_DATE(#{studyEndDay}, 'YYYY-MM-DD HH24:MI:SS')
, STUDY_START_TIME = #{studyStartTime}
, STUDY_END_TIME = #{studyEndTime}
, STUDY_LIMIT = #{studyLimit}
, STUDY_DEADLINE = TO_DATE(#{studyDeadline}, 'YYYY-MM-DD HH24:MI:SS')
, STUDY_SHOWPH = #{studyShowph}
, STUDY_CATEGORY = #{studyCategory}
, STUDY_EDIT_DATE = SYSDATE
WHERE STUDY_NUM = #{studyNum}

mapper.insert:
INSERT INTO TBL_STUDY_APPLY tsa(
	STUDY_APPLY_NUM 
	, STUDY_NUM 
	, USER_NUM 
	, USER_DETERMINATION 
) values (
	SEQ_STUDY_APPLY.NEXTVAL
	, #{studyNum}
	, #{userNum}
	, #{userDetermination}
	)
