STUDY_NUM
, USER_NUM
, STUDY_TITLE
, STUDY_DESCRIPT
, STUDY_LOCATION
, STUDY_START_DAY
, STUDY_END_DAY
, STUDY_START_TIME
, STUDY_END_TIME
, STUDY_LIMIT
, STUDY_DEADLINE
, STUDY_SHOWPH
, STUDY_CATEGORY
, STUDY_UPLOAD_DATE
, STUDY_EDIT_DATE) 
VALUES (
SEQ_STUDY.NEXTVAL
, #{userNum}
, #{studyTitle}
, #{studyDescript}
, #{studyLocation}
, TO_DATE(#{studyStartDay}, 'YYYY-MM-DD HH24:MI:SS')
, TO_DATE(#{studyEndDay}, 'YYYY-MM-DD HH24:MI:SS')
, #{studyStartTime}
, #{studyEndTime}
, #{studyLimit}
, #{studyDeadline}
, #{studyShowph}
, #{studyCategory}
, SYSDATE
, SYSDATE
)
